w3 = Web3(HTTPProvider('https://mainnet.infura.io/v3/8344fd70eef24c50b3fa252322585913'))
bpt_sc = w3.eth.contract('0x1Dc2948B6dB34E38291090B825518C1E8346938B', abi=bpt_abi)
uniswap_router = w3.eth.contract('0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D', abi=uniswap_router_abi)
amm_sc = w3.eth.contract('0xD77700fC3C78d1Cb3aCb1a9eAC891ff59bC7946D', abi=amm_abi)
dea_sc = w3.eth.contract('0x80aB141F324C3d6F2b18b030f1C4E95d4d658778', abi=dea_abi)

##################################
#			FUNCTIONS		 	 #
##################################

def get_eth_price():
	binance = ccxt.binance()
	binance_price = binance.fetch_ticker(f'ETH/USDT')
	return float(binance_price['info']['lastPrice'])

def bpt2eth(bpt_amount, sdea_amount, sdeus_amount):
	##################################
	#			VARIABLES		 	 #
	##################################
	dea_in_pool = dea_sc.functions.balanceOf('0x1Dc2948B6dB34E38291090B825518C1E8346938B').call()
	dea_weight = 19387755102040500000
	bpt_supply = bpt_sc.functions.totalSupply().call()
	total_weight = 50000000000000000000
	bpt_amount = int(bpt_amount * 1e18)
	swap_fee = 10000000000000000
	path = ['0x80aB141F324C3d6F2b18b030f1C4E95d4d658778', '0x3b62F3820e0B035cc4aD602dECe6d796BC325325']
	eth_price = get_eth_price()
	
	##################################
	#			CALCULATION		 	 #
	##################################
	dea_amount = bpt_sc.functions.calcSingleInGivenPoolOut(dea_in_pool, dea_weight, bpt_supply, total_weight, bpt_amount, swap_fee).call()
	print(round(dea_amount / 1e18, 2), '+',sdea_amount, 'dea')
	dea_amount = dea_amount + int(sdea_amount * 1e18)
	deus_amount = uniswap_router.functions.getAmountsOut(dea_amount, path).call()[-1]
	print(round(deus_amount / 1e18, 2), '+',sdeus_amount, 'deus')
	deus_amount = deus_amount + int(sdeus_amount * 1e18)
	eth_amount = amm_sc.functions.calculateSaleReturn(deus_amount).call()
	print(round(eth_amount / 1e18, 2), 'eth')
	print('eth price', eth_price)
	bpt_price = (eth_amount / bpt_amount)# * eth_price
	print('bpt price', round(bpt_price, 2))

import sys
bpt2eth(int(sys.argv[1]), 0, 0)

